import React from "react";
import { View ,Text,StyleSheet} from "react-native";

const muitosEstilos =() =>{
  return(
    <View style={estilos.conteiner}>
      <Text style={estilos.vermelho}>Meu Primeiro Teste com Vermelho-pequeno</Text>
      <Text>Meu Segundo teste com a cor Azul-Grande</Text>
      <Text>Meu Terceiro Teste com a cor Azul-Pequeno</Text>
      <Text>Meu Quarto Teste com a cor Vermelho-Grande</Text>
    </View>

  );
}
// toda vez em que fizer uma func√£o desse jeito acima vc deve importar ela mesma como default


